#!/usr/bin/env python

dna_string = ""

print('The length of the first  DNA sequence', len(dna_string))

print('The number of As is: ', dna_string.count('A'))
print('The number of Ts is: ', dna_string.count('T'))
print('The number of Gs is: ', dna_string.count('G'))
print('The number of Cs is: ', dna_string.count('C'))

print("converting to upper case:", "bird", "bird".upper())

dna_string2 = ""

print('The number of A and a is: ', dna_string2.upper().count('A'))
print('The number of T and t is: ', dna_string2.upper().count('T'))
print('The number of G and g is: ', dna_string2.upper().count('G'))
print('The number of C and c is: ', dna_string2.upper().count('C'))

dna_string3 = ""

print('Third DNA sequence\n' + dna_string3)
print('Third DNA sequence after T -> U substitution\n' + dna_string3.replace('T','U'))


dna_string4 = ""

print('Length of the fourth DNA sequence:', len(dna_string4))
print('Fourth DNA sequence\n' + dna_string4)
print('Fourth DNA sequence after T->U and t->u replacement\n' + dna_string4.replace('T', 'U').replace('t', 'u'))

content_AT = (dna_string4.upper().count('A') + dna_string4.upper().count('T')) / len(dna_string4) 
content_CG = (dna_string4.upper().count('C') + dna_string4.upper().count('G')) / len(dna_string4) 

print('AT content of the fourth DNA sequence:', content_AT)
print('CG content of the fourth DNA sequence:', content_CG)

dna_string5 = ""

print('Nucleotides 100-200  of the fifth DNA sequence\n' + dna_string5[99:200])
print('Number of Gs in this substring:', dna_string5[99:200].count('G'))

dna_string6 = ""

print('Nucleotides 100-200 of the sixth DNA sequence\n' + dna_string6[99:200])
print('Number of Gs in this substring:', dna_string6[99:200].upper().count('G'))

print('forvard string\n' + dna_string6[99:200].upper())
print('reverse\n' + dna_string6[199:98:-1].upper())

rev_comp = dna_string6[199:98:-1].upper().replace('A','a').replace('T','A').replace('a','T').replace('G','g').replace('C','G').replace('g','C')
print('reverse complement\n' + rev_comp)

ecoR_start = dna_string6.upper().find('GAATTC') + 1
ecoR_end = ecoR_start + 5
print('EcoR start position is {} nucleotide  and end position is {} nucleotide'.format(ecoR_start, ecoR_end))
